import React, { useState, useEffect } from 'react';
import { Table, Button, Modal, Form, Input } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';

const targetUrl = 'https://localhost:7148/api/Product';

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [modalOpen, setModalOpen] = useState(false);
    const [newProductData, setNewProductData] = useState({ name: '', price: '' });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(targetUrl);
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    const handleEdit = (productId) => {
        // Implement your edit logic here
        console.log(`Edit product with ID ${productId}`);
    };

    const handleDelete = async (productId) => {
        try {
            const response = await fetch(`${targetUrl}/${productId}`, {
                method: 'DELETE',
            });

            if (response.ok) {
                const updatedResponse = await fetch(targetUrl);
                const updatedData = await updatedResponse.json();
                setProducts(updatedData);
            } else {
                console.error('Error deleting product');
            }
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    const handleNewProduct = () => {
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setModalOpen(false);
        setNewProductData({ name: '', price: '' });
    };

    const handleSaveNewProduct = async () => {
        try {
            const response = await fetch(targetUrl, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(newProductData),
            });

            if (response.ok) {
                const updatedResponse = await fetch(targetUrl);
                const updatedData = await updatedResponse.json();
                setProducts(updatedData);

                handleCloseModal();
            } else {
                console.error('Error saving new product');
            }
        } catch (error) {
            console.error('Error saving new product:', error);
        }
    };

    const handleInputChange = (e, { name, value }) => {
        setNewProductData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    return (
        <div>
            <Button primary onClick={handleNewProduct}>
                New Product
            </Button>

            <Table celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Name</Table.HeaderCell>
                        <Table.HeaderCell>Price</Table.HeaderCell>
                        <Table.HeaderCell>Actions</Table.HeaderCell>
                        <Table.HeaderCell>Actions</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>
                    {products.map((product) => (
                        <Table.Row key={product.id}>
                            <Table.Cell>{product.name}</Table.Cell>
                            <Table.Cell>{product.price}</Table.Cell>
                            <Table.Cell>
                                <Button icon="edit" color="yellow" onClick={() => handleEdit(product.id)}>
                                    <i className="edit icon"></i>
                                    <span style={{ marginRight: '5px' }}>Edit</span>
                                </Button>
                            </Table.Cell>
                            <Table.Cell>
                                <Button icon="delete" color="red" onClick={() => handleDelete(product.id)}>
                                    <i className="delete icon"></i>
                                    <span style={{ marginRight: '5px' }}>Delete</span>
                                </Button>
                            </Table.Cell>
                        </Table.Row>
                    ))}
                </Table.Body>
            </Table>

            <Modal open={modalOpen} onClose={handleCloseModal}>
                <Modal.Header>Create New Product</Modal.Header>
                <Modal.Content>
                    <Form>
                        <Form.Field>
                            <label>Name</label>
                            <Input
                                name="name"
                                value={newProductData.name}
                                onChange={handleInputChange}
                                placeholder="Enter name"
                            />
                        </Form.Field>
                        <Form.Field>
                            <label>Price</label>
                            <Input
                                name="price"
                                value={newProductData.price}
                                onChange={handleInputChange}
                                placeholder="Enter price"
                            />
                        </Form.Field>
                    </Form>
                </Modal.Content>
                <Modal.Actions>
                    <Button primary onClick={handleSaveNewProduct}>
                        Save
                    </Button>
                    <Button onClick={handleCloseModal}>Cancel</Button>
                </Modal.Actions>
            </Modal>
        </div>
    );
};

export default ProductList;
