import React, { useState, useEffect } from 'react';
import { Table, Button, Modal, Form, Input } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';


const targetUrl = 'https://localhost:7148/api/Customer';

const Customerlist = () => {
    const [customers, setCustomers] = useState([]);
    const [modalOpen, setModalOpen] = useState(false);
    const [newCustomerData, setNewCustomerData] = useState({ name: '', address: '' });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('https://localhost:7148/api/Customer');
                const data = await response.json();
                setCustomers(data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []); // Empty dependency array ensures the effect runs once on mount

    const handleEdit = (customerId) => {
        // Implement your edit logic here
        console.log(`Edit customer with ID ${customerId}`);
    };

    const handleDelete = (customerId) => {
        const confirmed = window.confirm('Are you sure you want to delete this customer?');

        if (confirmed) {
            // Implement your delete logic here
            console.log(`Delete customer with ID ${customerId}`);
        }
    };

    const handleNewCustomer = () => {
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setModalOpen(false);
        setNewCustomerData({ name: '', address: '' });
    };

    const handleSaveNewCustomer = async () => {
        try {
            const response = await fetch(targetUrl, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(newCustomerData),
            });

            if (response.ok) {
                // Fetch the updated customer list
                const updatedResponse = await fetch(targetUrl);
                const updatedData = await updatedResponse.json();
                setCustomers(updatedData);

                // Close the modal
                handleCloseModal();
            } else {
                console.error('Error saving new customer');
            }
        } catch (error) {
            console.error('Error saving new customer:', error);
        }
    };

    const handleInputChange = (e, { name, value }) => {
        setNewCustomerData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    return (
        <div>
            <Button primary onClick={handleNewCustomer}>
                New Customer
            </Button>

            <Table celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Name</Table.HeaderCell>
                        <Table.HeaderCell>Address</Table.HeaderCell>
                        <Table.HeaderCell>Actions</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>
                    {customers.map((customer) => (
                        <Table.Row key={customer.id}>
                            <Table.Cell>{customer.name}</Table.Cell>
                            <Table.Cell>{customer.address}</Table.Cell>
                            <Table.Cell>
                                <Button icon="edit" color="blue" onClick={() => handleEdit(customer.id)} />
                                <Button icon="delete" color="red" onClick={() => handleDelete(customer.id)} />
                            </Table.Cell>
                        </Table.Row>
                    ))}
                </Table.Body>
            </Table>

            <Modal open={modalOpen} onClose={handleCloseModal}>
                <Modal.Header>Create New Customer</Modal.Header>
                <Modal.Content>
                    <Form>
                        <Form.Field>
                            <label>Name</label>
                            <Input
                                name="name"
                                value={newCustomerData.name}
                                onChange={handleInputChange}
                                placeholder="Enter name"
                            />
                        </Form.Field>
                        <Form.Field>
                            <label>Address</label>
                            <Input
                                name="address"
                                value={newCustomerData.address}
                                onChange={handleInputChange}
                                placeholder="Enter address"
                            />
                        </Form.Field>
                    </Form>
                </Modal.Content>
                <Modal.Actions>
                    <Button primary onClick={handleSaveNewCustomer}>
                        Save
                    </Button>
                    <Button onClick={handleCloseModal}>Cancel</Button>
                </Modal.Actions>
            </Modal>
        </div>
    );
};

export default Customerlist;
