import React, { useState, useEffect } from 'react';
import { Table, Button, Modal, Form, Input, Dropdown } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';

const targetUrl = 'https://localhost:7148/api/Sale';

const SalesList = () => {
    const [sales, setSales] = useState([]);
    const [modalOpen, setModalOpen] = useState(false);
    const [newSaleData, setNewSaleData] = useState({
        customerId: '',
        productId: '',
        storeId: '',
        dateSold: '',
    });

    const [customers, setCustomers] = useState([]);
    const [products, setProducts] = useState([]);
    const [stores, setStores] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const salesResponse = await fetch(targetUrl);
                const salesData = await salesResponse.json();
                setSales(salesData);

                // Fetch data for dropdowns (customers, products, stores)
                const customersResponse = await fetch('https://localhost:7148/api/Customer');
                const customersData = await customersResponse.json();
                setCustomers(customersData);

                const productsResponse = await fetch('https://localhost:7148/api/Product');
                const productsData = await productsResponse.json();
                setProducts(productsData);

                const storesResponse = await fetch('https://localhost:7148/api/Store');
                const storesData = await storesResponse.json();
                setStores(storesData);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    const handleEdit = (saleId) => {
        // Implement your edit logic here
        console.log(`Edit sale with ID ${saleId}`);
    };

    const handleDelete = (saleId) => {
        // Implement your delete logic here
        console.log(`Delete sale with ID ${saleId}`);
    };

    const handleNewSale = () => {
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setModalOpen(false);
        setNewSaleData({
            customerId: '',
            productId: '',
            storeId: '',
            dateSold: '',
        });
    };

    const handleSaveNewSale = async () => {
        try {
            const response = await fetch(targetUrl, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(newSaleData),
            });

            if (response.ok) {
                const updatedResponse = await fetch(targetUrl);
                const updatedData = await updatedResponse.json();
                setSales(updatedData);

                handleCloseModal();
            } else {
                console.error('Error saving new sale');
            }
        } catch (error) {
            console.error('Error saving new sale:', error);
        }
    };

    const handleInputChange = (e, { name, value }) => {
        setNewSaleData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    return (
        <div>
            <Button primary onClick={handleNewSale}>
                New Sale
            </Button>

            <Table celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Customer</Table.HeaderCell>
                        <Table.HeaderCell>Product</Table.HeaderCell>
                        <Table.HeaderCell>Store</Table.HeaderCell>
                        <Table.HeaderCell>Date Sold</Table.HeaderCell>
                        <Table.HeaderCell>Actions</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>
                    {sales.map((sale) => (
                        <Table.Row key={sale.id}>
                            <Table.Cell>{sale.customer.name}</Table.Cell>
                            <Table.Cell>{sale.produc}</Table.Cell>
                            <Table.Cell>{sale.storeName}</Table.Cell>
                            <Table.Cell>{sale.dateSold}</Table.Cell>
                            <Table.Cell>
                                <Button icon="edit" color="blue" onClick={() => handleEdit(sale.id)} />
                                <Button icon="delete" color="red" onClick={() => handleDelete(sale.id)} />
                            </Table.Cell>
                        </Table.Row>
                    ))}
                </Table.Body>
            </Table>

            <Modal open={modalOpen} onClose={handleCloseModal}>
                <Modal.Header>Create New Sale</Modal.Header>
                <Modal.Content>
                    <Form>
                        <Form.Field>
                            <label>Customer</label>
                            <Dropdown
                                name="customerId"
                                value={newSaleData.customerId}
                                options={customers.map((customer) => ({
                                    key: customer.id,
                                    text: customer.name,
                                    value: customer.id,
                                }))}
                                onChange={(e, { value }) =>
                                    handleInputChange(e, { name: 'customerId', value })
                                }
                                selection
                            />
                        </Form.Field>
                        <Form.Field>
                            <label>Product</label>
                            <Dropdown
                                name="productId"
                                value={newSaleData.productId}
                                options={products.map((product) => ({
                                    key: product.id,
                                    text: product.name,
                                    value: product.id,
                                }))}
                                onChange={(e, { value }) =>
                                    handleInputChange(e, { name: 'productId', value })
                                }
                                selection
                            />
                        </Form.Field>
                        <Form.Field>
                            <label>Store</label>
                            <Dropdown
                                name="storeId"
                                value={newSaleData.storeId}
                                options={stores.map((store) => ({
                                    key: store.id,
                                    text: store.name,
                                    value: store.id,
                                }))}
                                onChange={(e, { value }) =>
                                    handleInputChange(e, { name: 'storeId', value })
                                }
                                selection
                            />
                        </Form.Field>
                        <Form.Field>
                            <label>Date Sold</label>
                            <Input
                                name="dateSold"
                                type="date"
                                value={newSaleData.dateSold}
                                onChange={handleInputChange}
                            />
                        </Form.Field>
                    </Form>
                </Modal.Content>
                <Modal.Actions>
                    <Button primary onClick={handleSaveNewSale}>
                        Save
                    </Button>
                    <Button onClick={handleCloseModal}>Cancel</Button>
                </Modal.Actions>
            </Modal>
        </div>
    );
};

export default SalesList;
