import React, { useState, useEffect } from 'react';
import { Table, Button, Modal, Form, Input } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';

const targetUrl = 'https://localhost:7148/api/Store';

const StoreList = () => {
    const [stores, setStores] = useState([]);
    const [modalOpen, setModalOpen] = useState(false);
    const [newStoreData, setNewStoreData] = useState({ name: '', address: '' });
    const [deleteConfirmationOpen, setDeleteConfirmationOpen] = useState(false);
    const [storeIdToDelete, setStoreIdToDelete] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(targetUrl);
                const data = await response.json();
                setStores(data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    const handleEdit = (storeId) => {
        // Implement your edit logic here
        console.log(`Edit store with ID ${storeId}`);
    };

    const openDeleteConfirmation = (storeId) => {
        setStoreIdToDelete(storeId);
        setDeleteConfirmationOpen(true);
    };

    const confirmDelete = async () => {
        if (storeIdToDelete !== null) {
            try {
                const response = await fetch(`${targetUrl}/${storeIdToDelete}`, {
                    method: 'DELETE',
                });

                if (response.ok) {
                    const updatedResponse = await fetch(targetUrl);
                    const updatedData = await updatedResponse.json();
                    setStores(updatedData);

                    setDeleteConfirmationOpen(false);
                } else {
                    console.error('Error deleting store');
                }
            } catch (error) {
                console.error('Error deleting store:', error);
            }
        }
    };

    const handleNewStore = () => {
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setModalOpen(false);
        setNewStoreData({ name: '', address: '' });
    };

    const handleSaveNewStore = async () => {
        // Implement your save new store logic here
    };

    const handleInputChange = (e, { name, value }) => {
        setNewStoreData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    return (
        <div>
            <Button primary onClick={handleNewStore}>
                New Store
            </Button>

            <Table celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Name</Table.HeaderCell>
                        <Table.HeaderCell>Address</Table.HeaderCell>
                        <Table.HeaderCell>Actions</Table.HeaderCell>
                        <Table.HeaderCell>Actions</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>
                    {stores.map((store) => (
                        <Table.Row key={store.id}>
                            <Table.Cell>{store.name}</Table.Cell>
                            <Table.Cell>{store.address}</Table.Cell>
                            <Table.Cell>
                                <Button icon="edit" color="yellow" onClick={() => handleEdit(store.id)}>
                                    <i className="edit icon"></i>
                                    <span style={{ marginRight: '5px' }}>Edit</span>
                                </Button>
                            </Table.Cell>
                            <Table.Cell>
                                <Button icon="delete" color="red" onClick={() => openDeleteConfirmation(store.id)}>
                                    <i className="delete icon"></i>
                                    <span style={{ marginRight: '5px' }}>Delete</span>
                                </Button>
                            </Table.Cell>
                        </Table.Row>
                    ))}
                </Table.Body>
            </Table>

            <Modal open={modalOpen} onClose={handleCloseModal}>
                <Modal.Header>Create New Store</Modal.Header>
                <Modal.Content>
                    <Form>
                        <Form.Field>
                            <label>Name</label>
                            <Input
                                name="Name"
                                value={newStoreData.name}
                                onChange={handleInputChange}
                                placeholder="Enter name"
                            />
                        </Form.Field>
                        <Form.Field>
                            <label>Address</label>
                            <Input
                                name="Address"
                                value={newStoreData.address}
                                onChange={handleInputChange}
                                placeholder="Enter address"
                            />
                        </Form.Field>
                    </Form>
                </Modal.Content>
                <Modal.Actions>
                    <Button primary onClick={handleSaveNewStore}>
                        Save
                    </Button>
                    <Button onClick={handleCloseModal}>Cancel</Button>
                </Modal.Actions>
            </Modal>

            <Modal open={deleteConfirmationOpen} onClose={() => setDeleteConfirmationOpen(false)}>
                <Modal.Header>Confirm Delete</Modal.Header>
                <Modal.Content>
                    <p>Are you sure you want to delete this store?</p>
                </Modal.Content>
                <Modal.Actions>
                    <Button primary onClick={confirmDelete}>
                        Confirm
                    </Button>
                    <Button onClick={() => setDeleteConfirmationOpen(false)}>Cancel</Button>
                </Modal.Actions>
            </Modal>
        </div>
    );
};

export default StoreList;
