import React, { Component, useState } from 'react';
import './App.css';
import Test from './components/test';
import Customerlist from './components/customerlist';
import ProductList from './components/productlist';
import StoreList from './components/storelist';
import SalesList from './components/saleslist';
import Pagination from './components/pagination';

class App extends Component {
    state = {
        currentPage: 1,
    };

    changePage = (page) => {
        this.setState({ currentPage: page });
    };

    renderList = () => {
        const { currentPage } = this.state;
        const itemsPerPage = 10; // Set the number of items to display per page

        // Determine which list component to render based on the current tab
        switch (this.props.activeTab) {
            case 'Customer':
                return <Customerlist currentPage={currentPage} itemsPerPage={itemsPerPage} />;
            case 'Product':
                return <ProductList currentPage={currentPage} itemsPerPage={itemsPerPage} />;
            case 'Store':
                return <StoreList currentPage={currentPage} itemsPerPage={itemsPerPage} />;
            case 'Sales':
                return <SalesList currentPage={currentPage} itemsPerPage={itemsPerPage} />;
            default:
                return null;
        }
    };

    render() {
        return (
            <div className="App">
                <Test />
                {this.renderList()}
                <Pagination
                    currentPage={this.state.currentPage}
                    itemsPerPage={10} // Set the number of items to display per page
                    totalItems={100} // Replace with the total number of items in your list
                    onPageChange={this.changePage}
                />
            </div>
        );
    }
}

export default App;
